
public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
System.out.println("addition is:"+add(10,2));
System.out.println("subtraction is:"+sub(10,2));
System.out.println("multiplication is:"+mul(10,2));
System.out.println("division is:"+div(10,2));



	}
	public static int add(int num1,int num2) {
		int result=num1+num2;
		return result;
	}
	public static int sub(int numb1,int numb2) {
		int result=numb1-numb2;
		return result;
}
	public static int mul(int n1,int n2) {
		int res=n1*n2;
		return res;
	}
		
		public static int div(int a,int b) {
			return a/b;
			
			
}
}